import{o as e,c as a,b as r}from"./app.b2aa9d0b.js";const i='{"title":"GUI 가능 최소 서버","description":"","frontmatter":{"lang":"ko-KR","title":"GUI 가능 최소 서버","navbar":true,"sidebar":"auto"},"headers":[{"level":2,"title":"요구사항","slug":"요구사항"},{"level":2,"title":"우분투 이미지","slug":"우분투-이미지"},{"level":2,"title":"X 윈도우","slug":"x-윈도우"},{"level":3,"title":"설치","slug":"설치"},{"level":2,"title":"X 터미널","slug":"x-터미널"},{"level":2,"title":"편집기","slug":"편집기"},{"level":2,"title":"SSHD","slug":"sshd"},{"level":2,"title":"웹서버","slug":"웹서버"}],"relativePath":"ko/win_bridge_server/index.md","lastUpdated":1627214961110}',l={},t=r('<h1 id="gui-가능-최소-서버"><a class="header-anchor" href="#gui-가능-최소-서버" aria-hidden="true">#</a> GUI 가능 최소 서버</h1><h2 id="요구사항"><a class="header-anchor" href="#요구사항" aria-hidden="true">#</a> 요구사항</h2><ul><li>작고 가벼울 것 <ul><li>Vultr의 가상 머신에 설치</li><li>디스크상에 차지하는 크기가 작을 것</li><li>메모리를 작게 쓸 것</li><li>CPU 파워가 크게 필요하지 않을 것</li></ul></li><li>X 윈도우 <ul><li>MS 윈도우즈 응용 프로그램을 실행하려고 하기 때문에 GUI 필수</li><li>X Forwarding</li></ul></li><li>관리가 용이할 것 <ul><li><a href="https://www.vultr.com/" target="_blank" rel="noopener noreferrer">Vultr</a><ul><li>$100등 프로모션이 많으니 검색</li></ul></li><li><a href="https://ubuntu.com/" target="_blank" rel="noopener noreferrer">Ubuntu</a></li></ul></li></ul><h2 id="우분투-이미지"><a class="header-anchor" href="#우분투-이미지" aria-hidden="true">#</a> 우분투 이미지</h2><ul><li><a href="http://archive.ubuntu.com/ubuntu/dists/focal/main/installer-amd64/current/legacy-images/netboot/mini.iso" target="_blank" rel="noopener noreferrer">Ubuntu 20.04 focal mini.iso</a></li><li><code>$ du / -sh</code> =&gt; 3.7 GB</li></ul><h2 id="x-윈도우"><a class="header-anchor" href="#x-윈도우" aria-hidden="true">#</a> X 윈도우</h2><h4 id="윈도우-매니저"><a class="header-anchor" href="#윈도우-매니저" aria-hidden="true">#</a> 윈도우 매니저</h4><ul><li><a href="https://wiki.debian.org/WindowManager" target="_blank" rel="noopener noreferrer">종류</a></li><li><a href="https://en.wikipedia.org/wiki/Comparison_of_X_window_managers" target="_blank" rel="noopener noreferrer">비교</a></li><li><a href="https://ice-wm.org/" target="_blank" rel="noopener noreferrer">IceWM</a><ul><li>가벼운 WM중에 살아 있는 프로젝트</li></ul></li></ul><h4 id="디스플레이-매니저-로그인"><a class="header-anchor" href="#디스플레이-매니저-로그인" aria-hidden="true">#</a> 디스플레이 매니저 (로그인)</h4><ul><li><a href="https://wiki.debian.org/DisplayManager" target="_blank" rel="noopener noreferrer">종류</a></li><li><a href="https://en.wikipedia.org/wiki/SLiM" target="_blank" rel="noopener noreferrer">SLiM</a><ul><li>이름대로 가벼움</li><li>2013년 이후 더 이상 개발 없음</li><li>현재 Ubuntu focal(20.04)에서 문제 없이 동작</li><li>필자는 DM이 불필요해서 삭제함</li></ul></li></ul><h3 id="설치"><a class="header-anchor" href="#설치" aria-hidden="true">#</a> 설치</h3><p><a href="https://askubuntu.com/questions/1256285/installing-xorg-without-gnome" target="_blank" rel="noopener noreferrer">주의:</a> xinit은 반드시 윈도우 매니저(IceWM)를 먼저 설치하고 설치할 것. 그렇게 하지 않으면 xinit이 Gnome을 설치해 버림.</p><h4 id="x-윈도우-1"><a class="header-anchor" href="#x-윈도우-1" aria-hidden="true">#</a> X 윈도우</h4><p><code>$ sudo apt-get install xserver-xorg-core --no-install-recommends --no-install-suggests</code></p><ul><li>크기: 393 MB</li></ul><h4 id="icewm"><a class="header-anchor" href="#icewm" aria-hidden="true">#</a> IceWM</h4><p><code>$ sudo apt-get install icewm --no-install-recommends --no-install-suggests</code></p><ul><li>크기: 14.3 MB</li></ul><h4 id="xinit"><a class="header-anchor" href="#xinit" aria-hidden="true">#</a> xinit</h4><p><code>$ sudo apt-get install xinit</code></p><ul><li>크기: 37.7 MB</li></ul><h4 id="후-처리"><a class="header-anchor" href="#후-처리" aria-hidden="true">#</a> 후 처리</h4><ul><li>필자처럼 데스크탑 환경이 필요 없는 경우 <ul><li><code>$ sudo apt-get remove icewm --purge</code></li></ul></li><li>데스크탑 환경이 필요한 경우, SLiM 설치</li></ul><h4 id="slim-optional"><a class="header-anchor" href="#slim-optional" aria-hidden="true">#</a> <s>SLiM (Optional)</s></h4><p><code>$ sudo apt-get install slim</code></p><ul><li>크기: 5,494 KB</li><li>GUI로 로그인 하지 않기 때문에 Display Manager 불필요</li></ul><h2 id="x-터미널"><a class="header-anchor" href="#x-터미널" aria-hidden="true">#</a> X 터미널</h2><p>처음에는 rxvt-unicode를 시험해 봤으나 크기(65.4 MB)가 작지도 않으면서 많이 불편</p><h4 id="mate-terminal"><a class="header-anchor" href="#mate-terminal" aria-hidden="true">#</a> mate-terminal</h4><p><code>$ sudo apt-get install mate-terminal</code></p><ul><li>크기: 83.5 MB</li><li><code>$ XAUTHORITY=/home/USER/.Xauthority sudo mate-terminal</code> 안됨</li></ul><h4 id="qterminal"><a class="header-anchor" href="#qterminal" aria-hidden="true">#</a> <a href="https://www.pclinuxos.com/forum/index.php/topic,150127.0.html" target="_blank" rel="noopener noreferrer">qterminal</a></h4><ul><li>48.8 MB (?)</li><li><code>$ XAUTHORITY=/home/USER/.Xauthority sudo qterminal</code> OK</li></ul><h4 id="prompt"><a class="header-anchor" href="#prompt" aria-hidden="true">#</a> prompt</h4><ul><li><a href="https://www.cyberciti.biz/tips/howto-linux-unix-bash-shell-setup-prompt.html?__cf_chl_captcha_tk__=pmd_c7649102cfbca71e8e7f46aec7db9f23f14ee6aa-1627212579-0-gqNtZGzNAyKjcnBszQii" target="_blank" rel="noopener noreferrer">PS1</a></li><li><a href="https://linuxcommand.org/lc3_adv_tput.php" target="_blank" rel="noopener noreferrer">tput</a></li></ul><h2 id="편집기"><a class="header-anchor" href="#편집기" aria-hidden="true">#</a> 편집기</h2><h4 id="gvim"><a class="header-anchor" href="#gvim" aria-hidden="true">#</a> gvim</h4><p><code>$ sudo apt-get install vim-gtk3</code></p><ul><li>크기: 95.1 MB</li></ul><h2 id="sshd"><a class="header-anchor" href="#sshd" aria-hidden="true">#</a> SSHD</h2><h4 id="설치-1"><a class="header-anchor" href="#설치-1" aria-hidden="true">#</a> 설치</h4><p><code>$ sudo apt-get install openssh-server</code></p><ul><li>크기: 6,121 KB</li></ul><h4 id="x-forwarding-설정"><a class="header-anchor" href="#x-forwarding-설정" aria-hidden="true">#</a> X Forwarding 설정</h4><ol><li><code>$ sudo vim /etc/ssh/sshd_config</code></li><li>X11Forwarding, X11DisplayOffset, X11UseLocalhost의 주석을 푼다.</li></ol><div class="language-"><pre><code>X11Forwarding yes\nX11DisplayOffset 10\nX11UseLocalhost yes\n</code></pre></div><ol start="3"><li><code>$ sudo systemctl restart sshd</code></li></ol><h4 id="x-forwarding-client"><a class="header-anchor" href="#x-forwarding-client" aria-hidden="true">#</a> X Forwarding Client</h4><p><code>$ ssh -Y [계정@]&lt;SERVER_URL&gt;</code></p><h4 id="root에서-x-forwarding"><a class="header-anchor" href="#root에서-x-forwarding" aria-hidden="true">#</a> <a href="https://wiki.archlinux.org/title/Running_GUI_applications_as_root" target="_blank" rel="noopener noreferrer">root에서 X Forwarding</a></h4><div class="language-"><pre><code>$ ssh -Y SERVER\n$ SERVER$ sudo xclock\n</code></pre></div><p><code>$ SERVER$ XAUTHORITY=/home/USER/.Xauthority sudo xclock</code></p><h2 id="웹서버"><a class="header-anchor" href="#웹서버" aria-hidden="true">#</a> <a href="https://coderrocketfuel.com/article/deploy-a-react-application-to-vultr" target="_blank" rel="noopener noreferrer">웹서버</a></h2>',53);l.render=function(r,i,l,o,n,h){return e(),a("div",null,[t])};export default l;export{i as __pageData};
