import{o as e,c as i,b as a}from"./app.b2aa9d0b.js";const r='{"title":"X Window Least Server","description":"","frontmatter":{"title":"X Window Least Server","navbar":true,"sidebar":"auto"},"headers":[{"level":2,"title":"Requirements","slug":"requirements"},{"level":2,"title":"Ubuntu ISO Image","slug":"ubuntu-iso-image"},{"level":2,"title":"X Window System","slug":"x-window-system"},{"level":3,"title":"Intallation Order","slug":"intallation-order"},{"level":2,"title":"X Terminal","slug":"x-terminal"},{"level":2,"title":"Editor","slug":"editor"},{"level":2,"title":"SSHD","slug":"sshd"}],"relativePath":"win_bridge_server/index.md","lastUpdated":1623506359422}',l={},t=a('<h1 id="x-window-least-server"><a class="header-anchor" href="#x-window-least-server" aria-hidden="true">#</a> X Window Least Server</h1><h2 id="requirements"><a class="header-anchor" href="#requirements" aria-hidden="true">#</a> Requirements</h2><ul><li>Lightweight <ul><li>Fit to a virtual machine (Cheap price plan)</li><li>Small foot print</li><li>Low memory usage</li><li>Lower CPU power needed</li></ul></li><li>X Window <ul><li>Availability of GUI is mandatory since I use libraries(DLL) for MS Windows</li><li>X Forwarding</li></ul></li><li>Easy to manage <ul><li><a href="https://www.vultr.com/" target="_blank" rel="noopener noreferrer">Vultr</a><ul><li>Search for Vultr Promotion Code</li></ul></li><li><a href="https://ubuntu.com/" target="_blank" rel="noopener noreferrer">Ubuntu</a></li></ul></li></ul><h2 id="ubuntu-iso-image"><a class="header-anchor" href="#ubuntu-iso-image" aria-hidden="true">#</a> Ubuntu ISO Image</h2><ul><li><a href="http://archive.ubuntu.com/ubuntu/dists/focal/main/installer-amd64/current/legacy-images/netboot/mini.iso" target="_blank" rel="noopener noreferrer">Ubuntu 20.04 focal mini.iso</a></li><li><code>$ du / -sh</code> =&gt; 3.7 GB</li></ul><h2 id="x-window-system"><a class="header-anchor" href="#x-window-system" aria-hidden="true">#</a> X Window System</h2><h4 id="window-manager"><a class="header-anchor" href="#window-manager" aria-hidden="true">#</a> Window Manager</h4><ul><li><a href="https://wiki.debian.org/WindowManager" target="_blank" rel="noopener noreferrer">List</a></li><li><a href="https://en.wikipedia.org/wiki/Comparison_of_X_window_managers" target="_blank" rel="noopener noreferrer">Comparison</a></li><li><a href="https://ice-wm.org/" target="_blank" rel="noopener noreferrer">IceWM</a><ul><li>One of active projects for lightweight WM</li></ul></li></ul><h4 id="display-manager-login"><a class="header-anchor" href="#display-manager-login" aria-hidden="true">#</a> Display Manager(Login)</h4><ul><li><a href="https://wiki.debian.org/DisplayManager" target="_blank" rel="noopener noreferrer">List</a></li><li><a href="https://en.wikipedia.org/wiki/SLiM" target="_blank" rel="noopener noreferrer">SLiM</a><ul><li>Light as its name</li><li>No more development since 2013</li><li>No problem for Ubuntu focal(20.04) until now</li><li>Removed. Actually I don&#39;t need DM.</li></ul></li></ul><h3 id="intallation-order"><a class="header-anchor" href="#intallation-order" aria-hidden="true">#</a> Intallation Order</h3><p><a href="https://askubuntu.com/questions/1256285/installing-xorg-without-gnome" target="_blank" rel="noopener noreferrer">Warn:</a> It is important to install in order. Install IceWM first before installing xinit. Otherwise, xinit will install all the Gnome.</p><h4 id="x-window"><a class="header-anchor" href="#x-window" aria-hidden="true">#</a> X Window</h4><p><code>$ sudo apt-get install xserver-xorg-core --no-install-recommends --no-install-suggests</code></p><ul><li>Size: 393 MB</li></ul><h4 id="icewm"><a class="header-anchor" href="#icewm" aria-hidden="true">#</a> IceWM</h4><p><code>$ sudo apt-get install icewm --no-install-recommends --no-install-suggests</code></p><ul><li>Size: 14.3 MB</li></ul><h4 id="xinit"><a class="header-anchor" href="#xinit" aria-hidden="true">#</a> xinit</h4><p><code>$ sudo apt-get install xinit</code></p><ul><li>Size: 37.7 MB</li></ul><h4 id="postinstall"><a class="header-anchor" href="#postinstall" aria-hidden="true">#</a> Postinstall</h4><ul><li>When you don&#39;t need a desktop environmet like me. <ul><li><code>$ sudo apt-get remove icewm --purge</code></li></ul></li><li>Otherwise, install DM(SLiM).</li></ul><h4 id="slim-optional"><a class="header-anchor" href="#slim-optional" aria-hidden="true">#</a> SLiM (Optional)</h4><p><code>$ sudo apt-get install slim</code></p><ul><li>Size: 5,494 KB</li></ul><h2 id="x-terminal"><a class="header-anchor" href="#x-terminal" aria-hidden="true">#</a> X Terminal</h2><p>I tried rxvt-unicode(65.4 MB) first. It soon turned out to be frustrating.</p><h4 id="mate-terminal"><a class="header-anchor" href="#mate-terminal" aria-hidden="true">#</a> mate-terminal</h4><p><code>$ sudo apt-get install mate-terminal</code></p><ul><li>Size: 83.5 MB</li></ul><h2 id="editor"><a class="header-anchor" href="#editor" aria-hidden="true">#</a> Editor</h2><h4 id="gvim"><a class="header-anchor" href="#gvim" aria-hidden="true">#</a> gvim</h4><p><code>$ sudo apt-get install vim-gtk3</code></p><ul><li>Size: 95.1 MB</li></ul><h2 id="sshd"><a class="header-anchor" href="#sshd" aria-hidden="true">#</a> SSHD</h2><h4 id="installation"><a class="header-anchor" href="#installation" aria-hidden="true">#</a> Installation</h4><p><code>$ sudo apt-get install openssh-server</code></p><ul><li>Size: 6,121 KB</li></ul><h4 id="x-forwarding-configuration"><a class="header-anchor" href="#x-forwarding-configuration" aria-hidden="true">#</a> X Forwarding Configuration</h4><ol><li><code>$ sudo vim /etc/ssh/sshd_config</code></li><li>Uncomment X11Forwarding, X11DisplayOffset, and X11UseLocalhost</li></ol><div class="language-"><pre><code>X11Forwarding yes\nX11DisplayOffset 10\nX11UseLocalhost yes\n</code></pre></div><ol start="3"><li><code>$ sudo systemctl restart sshd</code></li></ol><h4 id="x-forwarding-client"><a class="header-anchor" href="#x-forwarding-client" aria-hidden="true">#</a> X Forwarding Client</h4><p><code>$ ssh -Y [Account@]&lt;SERVER_URL&gt;</code></p><h4 id="x-forwarding-as-root"><a class="header-anchor" href="#x-forwarding-as-root" aria-hidden="true">#</a> X Forwarding as root</h4><p>Running a GUI program as root using sudo or su results in an error.</p><ul><li>ex) <code>$ sudo xclock</code></li><li><a href="http://wiki.docking.org/index.php/How_to_access_X11_Forwarding_after_becoming_root" target="_blank" rel="noopener noreferrer">Add $DISPLAY to /root/.Xauthority</a><ul><li><code>$ sudo xauth add $(xauth -f ~generic_user/.Xauthority list | tail -1)</code></li></ul></li></ul>',48);l.render=function(a,r,l,n,o,s){return e(),i("div",null,[t])};export default l;export{r as __pageData};
